mapper.insert:
insert into tbl_review
(review_title, review_content, review_category, review_grade, group_id)
values(#{reviewTitle}, #{reviewContent}, #{reviewCategory}, #{reviewGrade}, #{groupId});

mapper.update:
UPDATE tbl_review
SET review_title= #{reviewTitle}, review_content = #{reviewContent} , review_category='', review_register_date=CURRENT_TIMESTAMP, review_update_date=CURRENT_TIMESTAMP, review_grade=5, group_id=0
WHERE review_id=0;
